g:

{{ name }}
{? use headHeight as height:
    {? if {{ attributes or methods }}:

        {? for attribute in attributes:
            {% set height {{ height + attribute.height }} %}

                {% view attribute %}

        ?}
    ?}
    {? if {{ methods }}:

        {? for method in methods:
            {% set height {{ height + method.height }} %}

                {% view method %}

        ?}
    ?}
?}
